INSERT INTO `oversea_house` VALUES ('17', '173', '31', '46', '58;56;55;54', '38', 'uploads/images/eRK5C4jvjM.jpg;uploads/images/znNFz3tGjo.jpg;uploads/images/uB9MyDdCi8.jpg;uploads/images/9TsOCAY2OS.jpg;uploads/images/SEewMLpPw0.jpg;uploads/images/qhoP5tfdzb.jpg;uploads/images/4w5HFDiMMh.jpg;uploads/images/htUcPgt0Gb.jpg', '<p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664193988440.jpg\" title=\"1616664193988440.jpg\" alt=\"1616664193988440.jpg\" width=\"600\" height=\"320\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217343522.jpg\" title=\"1616664217343522.jpg\" width=\"600\" height=\"338\" alt=\"1616664217343522.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217864981.jpg\" title=\"1616664217864981.jpg\" width=\"600\" height=\"338\" alt=\"1616664217864981.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217850652.jpg\" title=\"1616664217850652.jpg\" width=\"600\" height=\"338\" alt=\"1616664217850652.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217111495.jpg\" title=\"迪拜mag318\" width=\"600\" height=\"356\" alt=\"迪拜mag318\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217135261.jpg\" title=\"迪拜mag318\" width=\"600\" height=\"338\" alt=\"迪拜mag318\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664217351526.jpg\" title=\"迪拜公寓\" width=\"600\" height=\"338\" alt=\"迪拜公寓\"/></p>', '4.10', '入住MAG 318，与哈利法塔做邻居！', '位于哈利法塔区——世界之最汇聚区，游客必经之处。13间底商、健身中心、游泳池、停车场、精品店、高档酒店、餐厅、高端会客厅、大堂等。', '1', '现房', '40㎡起', '1-3房', '165.00', '永久产权', '30.00', '8', '精装修配家电', '迪拜', '10', '1', '5988', '2020-03-01', '<p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 项目名称</span></strong><span style=\"font-size: 16px;\">：MAG 318</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 开发商</span></strong><span style=\"font-size: 16px;\">：MAG</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 占地面积</span></strong><span style=\"font-size: 16px;\">：57308平方米</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 户数</span></strong><span style=\"font-size: 16px;\">：439户</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 项目编号</span></strong><span style=\"font-size: 16px;\">：1891</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 总价</span></strong><span style=\"font-size: 16px;\">：165万人民币起</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 户型</span></strong><span style=\"font-size: 16px;\">：面积40㎡起</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 交房时间</span></strong><span style=\"font-size: 16px;\">：现房</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 楼层结构</span></strong><span style=\"font-size: 16px;\">：2B+1G+3P+19层</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px;\">• 产权</span></strong><span style=\"font-size: 16px;\">：永久产权无公摊面积</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616663465214943.jpg\" title=\"迪拜mag318公寓\" alt=\"迪拜mag318公寓\" width=\"720\" height=\"746\"/></p><p><br/></p>', '<p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616663631488410.jpg\" title=\"1616663631488410.jpg\" alt=\"1616663631488410.jpg\" width=\"600\" height=\"356\"/></p><p style=\"text-align: center;\">单间</p><p><br/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616663665134947.jpg\" title=\"1616663665134947.jpg\" alt=\"1616663665134947.jpg\" width=\"600\" height=\"358\"/></p><p style=\"text-align: center;\">1房</p><p><br/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616663690126272.jpg\" title=\"1616663690126272.jpg\" alt=\"1616663690126272.jpg\" width=\"600\" height=\"358\"/></p><p style=\"text-align: center;\">2房</p><p><br/></p>', '<p style=\"line-height: 3em;\"><span style=\"color: rgb(128, 100, 162);\"><strong><span style=\"font-size: 16px;\">1、位于哈利法塔区</span></strong></span><span style=\"font-size: 16px;\">——世界之最汇聚区，游客必经之处。</span></p><p style=\"line-height: 3em;\"><span style=\"color: rgb(128, 100, 162);\"><strong><span style=\"font-size: 16px;\">2、位于核心商圈</span></strong></span><span style=\"font-size: 16px;\">——本区域为迪拜办公楼最密集的区域，共有 240栋办公楼，729万平方米商业建筑面积， 世界500强企业已有超过400多家在此设立据点，目前已有超过30万白领在此区域工作。</span></p><p style=\"line-height: 3em;\"><span style=\"color: rgb(128, 100, 162);\"><strong><span style=\"font-size: 16px;\">3、位于玛瑞希商业湾</span></strong></span><span style=\"font-size: 16px;\">——这里享有商贾云集之地，货物枢纽之核心，城市形象之大堂的美誉。迪拜水运河项目将修建跨越12公里长的海滨长廊，是一个及住宅、休闲、购物娱乐为一体建筑项目。</span></p><p style=\"line-height: 3em;\"><strong><span style=\"font-size: 16px; color: rgb(128, 100, 162);\">4、位于迪拜运河前排</span></strong><span style=\"font-size: 16px;\">——面朝世界最高塔哈利法塔及迪拜核心区楼群，每一个单元均能坐拥迪拜河景及天际线双重美景。</span></p><p><br/></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">• 游艇码头 - <strong>1分钟</strong></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">•&nbsp;世界最高塔：哈利法塔 - <strong>5分钟</strong></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">•&nbsp;世界最大的购物中心：Dubai Mall - <strong>5分钟</strong></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">•&nbsp;迪拜歌剧院 - <strong>10分钟</strong></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">•&nbsp;迪拜国际金融中心 - <strong>10分钟</strong></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">•&nbsp;迪拜国际机场 - <strong>15分钟</strong></span></p><p><br/></p>', '<section><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important; vertical-align: middle; display: inline-block; color: rgb(31, 73, 125);\"><section><p><span style=\"font-size: 20px; color: rgb(128, 100, 162);\"><strong>丰富的社区生活服务</strong></span></p></section></span></section><p><br/></p><section><section><p style=\"text-align: left;\"><span style=\"font-size: 16px;\">13间底商、健身中心、游泳池、停车场、精品店、高档酒店、餐厅、高端会客厅、大堂等</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210325/1616664386252295.jpg\" title=\"迪拜公寓投资\" alt=\"迪拜公寓投资\" width=\"720\" height=\"550\"/></p><p><br/></p></section></section>', '<p style=\"line-height: 2em;\"><strong style=\"color: rgb(128, 100, 162); font-size: 20px;\">无与伦比的项目优势</strong><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important; vertical-align: middle; display: inline-block; color: rgb(31, 73, 125);\"></span></p><p><br/></p><section><section><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">1、一房两房包租<span style=\"font-size: 18px; color: rgb(128, 100, 162);\"><strong>3</strong></span>年，每年<span style=\"font-size: 18px; color: rgb(128, 100, 162);\"><strong>8%</strong></span></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">2、项目最大的优势之一就在于它的地理位置，<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>紧邻哈利法塔与迪拜运河，处于最中心的CBD商业区</strong></span>，稀缺的小户型，具有绝佳的保值增值价值及稳定的租金回报率。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">3、项目是精装修，<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>家电全采用国际知名品牌</strong></span>，交付之后可拎包入住。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">4、项目开发商MAG集团成立于1978年，拥有40年历史，是<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>海湾地区前六的开发商</strong></span>，旗下项目以建筑质量高，设计时尚现代著称。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">5、项目<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>南面是迪拜运河景观</strong></span>，<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>背面是迪拜最高塔哈利法塔</strong></span>；距离游艇港仅50米。位于核心地段-哈利法塔区，汇聚世界之最，游客必经之地；拥有240栋办公楼、729万㎡建筑面积、82%商业、30万白领；投资环境一流。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 16px;\">6、<span style=\"color: rgb(128, 100, 162); font-size: 18px;\"><strong>交通十分便捷</strong></span>，紧邻迪拜运河，多条入口和出口，直接通迪拜主干道。</span></p></section></section><p><br/></p>', '2021-03-25 17:18:48', '2021-04-21 18:25:20');
