INSERT INTO `oversea_house` VALUES ('23', '228', '31', '46', '58;55;54;52', '37', 'uploads/images/ckRwaMiZXi.jpg;uploads/images/rSLbqZaslj.jpg;uploads/images/PapYicNpie.jpg;uploads/images/yLYokjteNa.jpg;uploads/images/ngNQeYi1Bi.jpg;uploads/images/Vik3RcDfNY.jpg;uploads/images/4VMz83XhJY.jpg', '<p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330100509.jpg\" title=\"1618476330100509.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330128729.jpg\" title=\"1618476330128729.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330506871.jpg\" title=\"1618476330506871.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330137271.jpg\" title=\"1618476330137271.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330422356.jpg\" title=\"1618476330422356.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330717813.jpg\" title=\"1618476330717813.jpg\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476330130709.jpg\" title=\"1618476330130709.jpg\"/></p><p><br/></p>', '3.00', '迪拜市中心学区房 Sobha Hartland', 'Sobha Hartland项目于2014年启动，耗资40亿美元。是拥有800万平方英尺的永久产权社区，其中30%(约240万平方英尺)作为绿化区域，势必将成为迪拜新的绿洲。', '1', '预计2021年', '44㎡起', 'Studio、1~2房', '138.00', '永久产权', '30.00', '8', '精装送车位', '迪拜美丹区', '1', '1', '1916', '2020-02-12', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">• 建筑：3期-G+2层停车场+12层 &nbsp;/ &nbsp;4期-G+2层停车场+28层</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;位置：迪拜美丹区</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;房型：Studio、1房、2房</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;面积：净面积44-207平方米</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;价格：770,000迪拉姆起</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;单位数量：1500个单位</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;停车位：买房送车位</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;落成日期：3期-2019年第四季度交楼 &nbsp;/ &nbsp;4期-预计2021年交楼</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;租金收益：预计年租金收益率8%以上</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;交房规格：精装修、天地壁厨卫、送部分德国品牌家电</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;公共设施：豪华入门大堂、30%社区绿化配套、商业街、健身中心、咖啡馆、瑜伽中心、美容院、游泳池、网球场、清真寺、五星级酒店、英国前五私立国际学校、托儿所</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476260138157.jpg\" title=\"1618476260138157.jpg\" alt=\"9.jpg\"/></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><br/></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">•&nbsp;项目亮点：</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">1、SOBHA迪拜皇家御用开发商，项目品质有保障，30%的绿化，毗邻迪拜皇宫</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">2、两所顶级英制贵族学校（运营中），北伦敦学校（英国私立国际学校前五）</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">3、迪拜新市中心，打造中东第一商业区</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">4、濒临2条人工运河，游艇贯穿市中心，汇聚3条地铁线</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">5、0公摊，永久产权，精装送车位</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">6、购房可获得迪拜永居权</span></p><p><br/></p><p style=\"line-height: 1.75em; text-align: center;\"><span style=\"color: rgb(227, 108, 9);\"><strong><span style=\"font-size: 16px;\">美丹SOBHA Hartland</span></strong></span></p><p style=\"line-height: 1.75em; text-align: center;\"><span style=\"color: rgb(227, 108, 9);\"><strong><span style=\"color: rgb(227, 108, 9); font-size: 16px;\">低密度高绿化社区，市中心学区房</span></strong></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size: 16px;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210415/1618476295630845.jpg\" title=\"1618476295630845.jpg\" alt=\"0.jpg\"/></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><br/></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">迪拜新运河也正在计划被打造成为迪拜又一个旅游景点之一，这个全长12公里，耗资37亿迪拉姆，贯穿迪拜市中心的人工河项目将为迪拜带来更多的便利和商机。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">而在这样潜力无限的全新商区，Sobha Hartland则是其中唯一个具有永久产权的别墅、四合院住宅和公寓社区。因其宽敞舒适的居住环境，极具吸引力的付款计划、布局合理的别墅、公寓及酒店、和位于迪拜独一无二的中心位置，相信将会成为居住者和投资客的热门房产。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">作为一家跨国公司，Sobha Group已经在阿联酋、阿曼、卡塔尔、巴林、文莱和印度投资并发展。在过去40年的发展中，Sobha Group已经成为海湾合作委员会最值得信赖和尊重的房地产公司之一。同时跻身迪拜十大开发商。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">Sobha Hartland项目于2014年启动，耗资40亿美元。是拥有800万平方英尺的永久产权社区，其中30%(约240万平方英尺)作为绿化区域，势必将成为迪拜新的绿洲。它也是迪拜运河边唯一一个别墅区，而优越的地理位置使Sobha Hartland的居民仅需要几分钟即可抵达DUBAI MALL，哈利法塔，美丹赛马场和Ras Al Khor自然保护区。不仅如此，Sobha Hartland还有地铁相连，即便是公共交通也非常便利。</span></p><p><br/></p>', '<p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754177118311.png\" title=\"1619754177118311.png\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754177622454.png\" title=\"1619754177622454.png\"/></p><p style=\"text-align: center;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754177113930.png\" title=\"1619754177113930.png\"/></p><p><br/></p>', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">ohammed Bin Rashid City，是由迪拜酋长发起打造的大型住宅城区，紧靠迪拜Downtown，Meydan占地达6700万平方尺，发展计划包括商业会议设施、五星级酒店、高尔夫球场等。在建的运河也将把Meydan和Downtown、Business Bay这三个区域连接在一起。位于Nad Al Sheba的王室行宫也紧邻Meydan。首屈一指的高档住宅区域SOBHA HARTLAND就位于Meydan跑马场区域。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754327102673.jpg\" title=\"1619754327102673.jpg\" alt=\"1619754327102673.jpg\" width=\"850\" height=\"619\"/></p><p style=\"line-height: 1.75em;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754340911585.png\" title=\"1619754340911585.png\" alt=\"1619754340911585.png\" width=\"850\" height=\"478\"/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">交通发达，一条运河、两条地铁、三条主要公路交汇处。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">迪拜运河在2016年11月开通后，一个月之内周边房价上涨20%，商务港区域的房产交易更是火爆异常；首霸心领地位于迪拜运河二期，预计2020年通航，未来升值潜力巨大。可乘坐极具迪拜特色的水上的士到达首霸心领地。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">迪拜无人驾驶地铁，现有绿线和红线，绿线将会修建延长线直达本社区，其中美丹站将修建人行天桥直通首霸公寓区；紫线由迪拜新机场经过首霸心领地连接旧机场，将在2020年建成。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">10分钟车程抵达哈利法塔；44号公路连接新老迪拜，去往迪拜新城区20分钟车程，迪拜传统老城区15分钟；44号公路分支Ras Al Khor路12分钟到达华人聚居地—龙城；66号公路可通往迪拜大学城、绿洲硅谷。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210430/1619754403831072.png\" title=\"1619754403831072.png\" alt=\"1619754403831072.png\" width=\"850\" height=\"618\"/></p><p style=\"line-height: 1.75em;\"><br/></p>', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">1、SOBHA 迪拜皇家御用开发商，项目品质有保障</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">Sobha最初由创办人PNC Menon于1976年在阿曼成立，1983年承建阿曼皇宫。在整个中东地区得到了很多承接大工程的设计机会，包括阿曼的热门景点苏丹卡布斯大清真寺和阿布扎比的安娜塔拉凯瑟尔艾尔萨拉沙漠度假酒店，凯宾斯基酒店，迪拜游艇会所等。一直以来都是迪拜的是大开发商之一，连续6年获得“客户满意度最高的开发商”，迪拜酋长城MBR别墅社区也是Sobha的项目之一。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">2、学区房，社区含有两所顶级英制贵族学校（North Landon 北伦敦分校、SOBHA 英式国际院校）</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">其中北伦敦学院在过去十年里两次被英国泰晤士报选为英国最优秀的五所私立学校之一，每年有超过50%的毕业生进入牛津、剑桥、哈佛、斯坦福等名校，并且北伦敦学院与迪拜华语学院合作，为学生提供优质的中文课程。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">两所学校距离我们的项目仅2分钟路程，学校共有1000余个学位，都是我们的租户群体。学校与首霸有合作，入读学费享有9折优惠。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">3、社区配套丰富，与富人为邻，周边汇聚新迪拜地标，打造中东第一商业富人区</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">该片区汇聚了迪拜所有的标志性建筑，世界最高的住宅楼、世界最大的购物商城-Meydan One Mall、世界最大的音乐喷泉、世界最高的旋转餐厅、国家一级湿地保护区、全球最长的室内滑雪场，世界上最昂贵的美丹赛马场等等。是迪拜市中心唯一一个集别墅、公寓、学校、五星级酒店和办公楼于一体的绿色综合社区，社区里拥有健身中心、网球场、清真寺、咖啡馆、L型别墅、瑜伽中心、五星酒店、美容院、公园、商场、游泳池、托儿所等丰富配套。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">4、迪拜唯一的滨河社区，30%的绿化，毗邻迪拜皇宫</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">在迪拜，一棵树的一年维护成本是5000美金，只有在皇室地区，才会有这么多的绿化。而且从社区出发，只需要坐水上巴士一个站，就可以抵达世界最大的购物商场Meydan One Mall</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">5、交通发达，一条运河、两条地铁、三条主要公路交汇处</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">迪拜运河在2016年11月开通后，一个月之内周边房价上涨20%，商务港区域的房产交易更是火爆异常；首霸心领地位于迪拜运河二期，预计2020年通航，未来升值潜力巨大。可乘坐极具迪拜特色的水上的士到达首霸心领地。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">迪拜无人驾驶地铁，现有绿线和红线，绿线将会修建延长线直达本社区，其中美丹站将修建人行天桥直通首霸公寓区；紫线由迪拜新机场经过首霸心领地连接旧机场，将在2020年建成。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">10分钟车程抵达哈利法塔；44号公路连接新老迪拜，去往迪拜新城区20分钟车程，迪拜传统老城区15分钟；44号公路分支Ras Al Khor路12分钟到达华人聚居地—龙城；66号公路可通往迪拜大学城、绿洲硅谷。</span></p><p><br/></p>', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">贷款政策</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">1.贷款条件：</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">满21岁，持有效护照，六个月银行流水即可。</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">2. 贷款比例：</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">海外投资者可贷50%-75%</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">3.贷款年限：</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">最长25年，不超过65周岁</span></p><p style=\"line-height: 1.75em;\"><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">4.银行利息约4.5%</span></p><p><br/></p>', '2021-04-15 16:27:57', '2021-05-10 16:40:55');
