INSERT INTO `oversea_house` VALUES ('27', '197', '33', '46', '58', '37', 'uploads/images/jIxxOlsnwy.jpg;uploads/images/BJ7zKtzN2V.jpg;uploads/images/WSbsahHoPK.jpg;uploads/images/ayLyuV727H.jpg;uploads/images/vncIIzr8Z5.jpg;uploads/images/BqEJQSDhyw.jpg;uploads/images/6xdoo2ojss.jpg', '<p><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635720127903.jpg\" title=\"1620635720127903.jpg\" width=\"850\" height=\"478\" alt=\"1620635720127903.jpg\"/></p><p><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635720350709.jpg\" title=\"1620635720350709.jpg\" width=\"850\" height=\"465\" alt=\"1620635720350709.jpg\"/></p><p><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635720316843.jpg\" title=\"1620635720316843.jpg\" width=\"850\" height=\"465\" alt=\"1620635720316843.jpg\"/></p><p><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635720126147.jpg\" title=\"1620635720126147.jpg\" width=\"850\" height=\"465\" alt=\"1620635720126147.jpg\"/></p><p><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635720405058.jpg\" title=\"1620635720405058.jpg\" width=\"850\" height=\"478\" alt=\"1620635720405058.jpg\"/></p><p><br/></p>', '3.00', '柬埔寨首创智能云端写字楼KY iCenter', 'KY-iCenter座落于金边繁华正核心-BKK商圈，金边政官达要、各大企业高管、金边富裕人士多居住于此，该区因大使馆林立更有「使馆特区」美名。区内多家跨国银行、企业总部、星级酒店、高档精致餐厅与大型现代购物中心，目前正规划兴建双捷运系统。', '2', '2021年', '42.7㎡', '单间', '100.00', '永久产权', '30.00', '7', '毛坯', '柬埔寨金边', '0', '1', '339', '2019-01-17', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">香港巨石集团在豪宅标的PICASSO CITY GARDEN大获成功后，今年再次慿藉独到眼光及高度资源整合能力，携手金边知名企业UC Group，共同推出金边首座甲级智能科技写字楼KY iCenter。其采用最新云端联网技术，打造3i科技写字楼– intelligent(智能)、instant(实时)与infinity(无界)。AI全智能技术、共享式商务空间、云信息同步服务、行动装置远程实时控制…等，打造先进、安全、便捷的智能工作环境。气派尊贵的礼宾接待中心、多样专业代办服务配合多语接待秘书，满足高端国际商务人士的需求。外观设计融合简约、前卫与环保理念打造成为立体垂直花园，呈现自然与人文的完美结合。KY iCenter秉持先进设计理念、突破创新，超越现有规格，势将引发金边写字楼一股科技革新浪潮！</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><br/></span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635675110970.png\" title=\"1620635675110970.png\" alt=\"微信截图_20210510163350.png\"/></span></p><p style=\"line-height: 1.75em;\"><br/></p>', '', '<section><section><section><section><section><section><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">BKK金融商业特区&nbsp;金边繁华正核心</span></p></section></section></section></section></section></section><p><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">KY-iCenter座落于金边繁华正核心-BKK商圈，金边政官达要、各大企业高管、金边富裕人士多居住于此，该区因大使馆林立更有「使馆特区」美名。区内多家跨国银行、企业总部、星级酒店、高档精致餐厅与大型现代购物中心，目前正规划兴建双捷运系统。安全居住的优良质量、便利完善的生活机能，使得BKK金融商业特区长期以来都是金边企业设点的首选精华路段。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\"><img src=\"http://ceshi.iaumeca.com:88/uploads/ueditor/image/20210510/1620635690149664.png\" title=\"1620635690149664.png\" alt=\"1620635690149664.png\" width=\"850\" height=\"592\"/></span></p><p><br/></p>', '<section><section><section><section><section><section><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">巨石集团引领指标&nbsp; 助柬埔寨与世界接轨</span></p></section></section></section></section></section></section><p><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">巨石集团总经理邓镇岳表示，巨石集团做为一专业金融平台，长期先于投资者洞悉市场变化，发掘值得投资的新市场，多年前即布局并深耕柬埔寨市场，现已逐渐开花结果。除了在金边推出首座艺术豪宅-「毕加索城市花园」成功热卖之外；结合当地资源所成立的「柬埔寨华商投资协会」，也扮演桥梁角色协助许多企业落地金边；今年，我们更成立了巨石人寿保险公司，提供民众更完整的医疗保障和理财选择。</span></p><p><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">KY iCenter这个项目我们选择与在当地拥有25年历史的知名企业UC Group合作，引进最新云端科技，打造柬埔寨第一座智能科技总部，相信这个项目将引领金边写字楼到更高水平，开创金边城市新格局。</span></p><p><br/></p>', '<p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">外资涌入&nbsp;新企狂增&nbsp;金边写字楼需求大缺口</span></p><p><br/></p><p style=\"line-height: 1.75em;\"><span style=\"font-size: 16px;\">区域经济移转东盟，柬埔寨仰赖全亚洲最开放经济政策与美元计价优势，近年经济飞涨，高效经济动能吸引国际企业进入投资，2017年外国直接投资(FDI)占GDP即高达12.39%，东盟仅次于新加坡。2010-2016年光是外资便在柬埔寨成立了19,000家新公司，随之而来的大量外派高管也支撑金边租金市场名列全球第四。</span></p><p><br/></p><p><span style=\"font-size: 16px;\">柬埔寨本地企业亦不煌多让，不但2017年高达4899家新公司设立，2018年第一季更较去年同期成长160%。需求大却供给少正是目前的市场写照，根据莱坊报告指出，现有金边写字楼面积为约37.7万平方米，入住率达90%以上，即便在2020年也仅增加17.5万平方米的空间，以目前新企狂增的成长力道，市场将极度的供不应求，金边写字楼市场即将燃起前所未有的热度。<br/></span></p><p><br/></p>', '2021-04-21 15:48:34', '2021-05-10 16:36:32');
